import "./App.css";
import React, { useState } from "react";
import Clock from "./components/Clock";
import TaskList from "./components/TaskList";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [priority, setPriority] = useState("low");
  const [filterPriority, setFilterPriority] = useState("all");
  const [filterText, setFilterText] = useState("");

  const handleAddTask = (e) => {
    e.preventDefault();
    if (newTask.trim() !== "") {
      setTasks((prevTasks) => [
        ...prevTasks,
        { content: newTask, priority: priority },
      ]);
      setNewTask("");
      setPriority("low");
    }
  };

  const handleDeleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  const handleEditTask = (index, newContent) => {
    const editedTasks = tasks.map((task, i) =>
      i === index ? { ...task, content: newContent } : task
    );
    setTasks(editedTasks);
  };

  const handleFilterPriorityChange = (e) => {
    setFilterPriority(e.target.value);
  };

  const handleFilterTextChange = (e) => {
    setFilterText(e.target.value);
  };

  const filteredTasks = tasks.filter((task) => {
    if (filterPriority === "all") {
      return task.content.toLowerCase().includes(filterText.toLowerCase());
    } else {
      return (
        task.priority === filterPriority &&
        task.content.toLowerCase().includes(filterText.toLowerCase())
      );
    }
  });

  return (
    <div className="App">
      <Clock />
      <div className="filters">
        <div>
          <label htmlFor="priorityFilter">Filtrar por prioridad:</label>
          <select
            id="priorityFilter"
            value={filterPriority}
            onChange={handleFilterPriorityChange}
          >
            <option value="all">Todas</option>
            <option value="low">Baja</option>
            <option value="medium">Media</option>
            <option value="high">Alta</option>
          </select>
        </div>
        <div>
          <label htmlFor="textFilter">Filtrar por contenido:</label>
          <input
            type="text"
            id="textFilter"
            value={filterText}
            onChange={handleFilterTextChange}
          />
        </div>
      </div>
      <form onSubmit={handleAddTask} className="form-container">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Nueva tarea"
        />
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="low">Baja</option>
          <option value="medium">Media</option>
          <option value="high">Alta</option>
        </select>
        <button type="submit">Agregar</button>
      </form>
      <TaskList
        tasks={filteredTasks}
        onDeleteTask={handleDeleteTask}
        onEditTask={handleEditTask}
      />
    </div>
  );
}

export default App;
